class Stack 
{
  constructor() {
    this.array = [];
    this.minArray = [];
    this.size = 0;
  }

  push(number) {
    let min = number;
    // Always keep minimum number in stack
    if (this.size !== 0 && min > this.minArray[this.size - 1]) {
      min = this.minArray[this.size - 1]
    }
    this.array.push(number);
    this.minArray.push(min);
    this.size++;
  }

  pop() {
    this.minArray.pop()
    this.size--;
    return this.array.pop()
  }

  show() {
    console.log(this.array);
    console.log(this.minArray);
  }
  min() {
    console.log('Min => ', this.minArray[this.size - 1]);
    return this.minArray[this.size - 1];
  }
}

const stack = new Stack();
stack.push(3);
stack.push(2);
stack.push(4);
stack.push(1);
stack.push(9);
stack.show();
stack.min(); // 1
stack.pop();
stack.pop();
stack.show(); 
stack.min(); // 2
